// Filter and score YouTube trending videos
const publishedAt = new Date($json.snippet.publishedAt);
const now = new Date();
const hoursDiff = (now - publishedAt) / (1000 * 60 * 60);

// Accept videos up to 7 days old (168 hours)
if (hoursDiff > 168) {
  return [];
}

const views = Number($json.statistics.viewCount) || 0;
const likes = Number($json.statistics.likeCount) || 0;
const comments = Number($json.statistics.commentCount) || 0;

const engagementRate = views > 0 ? ((likes + comments) / views) * 100 : 0;

// Boost videos that are very fresh
const ageBonus = hoursDiff < 12 ? 2 : (hoursDiff < 24 ? 1.5 : 1);

// Viral score calculation
const viralScore = (views * 0.5 * ageBonus) + ((likes * 30) + (comments * 50) + (engagementRate * 1000));

return [{
  json: {
    videoId: $json.id,
    title: $json.snippet.title,
    description: $json.snippet.description,
    channelTitle: $json.snippet.channelTitle,
    thumbnail: $json.snippet.thumbnails?.maxres?.url || $json.snippet.thumbnails?.high?.url || '',
    viewCount: views,
    likeCount: likes,
    commentCount: comments,
    engagementRate: engagementRate.toFixed(2),
    viralScore: Math.round(viralScore),
    ageHours: Math.round(hoursDiff),
    publishedAt: $json.snippet.publishedAt
  }
}];

