parameters.jsCode:
// Parse Gemini response and extract VEO3 prompt
const response = $input.first().json;
let veo3Prompt = '';

try {
  if (response.candidates && response.candidates[0]) {
    veo3Prompt = response.candidates[0].content.parts[0].text.trim();

    // Remove any markdown formatting
    veo3Prompt = veo3Prompt.replace(/```[a-z]*\n?/g, '').trim();

    // Remove "VEO 3 Prompt:" or similar prefixes
    veo3Prompt = veo3Prompt.replace(/^(VEO\s*3\s*Prompt:|Prompt:)\s*/i, '').trim();

    // Ensure max 480 characters
    if (veo3Prompt.length > 480) {
      veo3Prompt = veo3Prompt.substring(0, 477) + '...';
    }
  } else {
    throw new Error('No candidates in Gemini response');
  }
} catch (error) {
  // Fallback to a known-good prompt
  veo3Prompt = "A cinematic slow-motion wide shot of ocean waves crashing on coastal rocks at golden hour, professional 4K quality with shallow depth of field, warm color grading, dramatic backlit sunlight creating spray highlights, peaceful atmosphere.";
  console.log('Gemini parse error, using fallback:', error.message);
}

return [{
  json: {
    veo3Prompt: veo3Prompt,
    sourceVideo: {
      title: $('Get Top Video').item.json.title,
      channel: $('Get Top Video').item.json.channelTitle,
      views: $('Get Top Video').item.json.viewCount
    }
  }
}];

