// Parse operation ID from VEO3 response
const input = $input.first().json;
const operationName = input.name;
const operationId = operationName.split('/').pop();

const chatId = '1496380263'; // Telegram chat ID

return [{
  json: {
    operationName: operationName,
    operationId: operationId,
    checkUrl: `https://generativelanguage.googleapis.com/v1beta/${operationName}`,
    status: 'PROCESSING',
    retryCount: 0,
    maxRetries: 15,
    chatId: chatId,
    message: 'üé¨ Video √ºretimi ba≈üladƒ±! Bekleniyor...'
  }
}];

// Parse VEO3 status and extract video URI if ready
const statusResponse = $input.first().json;
const previousData = $('Parse Operation ID').item.json;
const chatId = previousData.chatId;

if (statusResponse.done === true) {
  // Video is ready! Extract URI using regex
  const responseStr = JSON.stringify(statusResponse.response);
  const uriMatch = responseStr.match(/"uri":"([^"]+)"/);  
  const videoUri = uriMatch ? uriMatch[1] : null;

  if (!videoUri) {
    return [{
      json: {
        status: 'ERROR',
        error: 'Video URI not found in response',
        shouldRetry: false,
        chatId: chatId
      }
    }];
  }

  return [{
    json: {
      status: 'DONE',
      videoUri: videoUri,
      shouldRetry: false,
      chatId: chatId,
      operationName: statusResponse.name,
      message: '‚úÖ Video hazƒ±r! ƒ∞ndiriliyor...'
    }
  }];
} else {
  // Still processing, check retry count
  const retryCount = (previousData.retryCount || 0) + 1;
  const maxRetries = previousData.maxRetries || 15;

  if (retryCount >= maxRetries) {
    return [{
      json: {
        status: 'TIMEOUT',
        error: `Video ${maxRetries} deneme sonrasƒ± hazƒ±r deƒüil`,
        shouldRetry: false,
        chatId: chatId,
        message: '‚è±Ô∏è Video √ºretimi √ßok uzun s√ºrd√º, zaman a≈üƒ±mƒ±.'
      }
    }];
  }

  return [{
    json: {
      operationName: previousData.operationName,
      operationId: previousData.operationId,
      checkUrl: previousData.checkUrl,
      status: 'PROCESSING',
      retryCount: retryCount,
      maxRetries: maxRetries,
      chatId: chatId,
      shouldRetry: true,
      message: `‚è≥ Hala i≈üleniyor... (${retryCount}/${maxRetries})`
    }
  }];
}
