// Login kontrolü ve izin günü hesaplama
const username = $input.first().json.body.username;
const password = $input.first().json.body.password;

// Sheets'ten gelen tüm satırlar
const allRows = $input.all();

// Kullanıcı bul (username ve password eşleşmeli)
const user = allRows.find(row => {
  const data = row.json;
  // Sheets'te username kolonu (örnek: A kolonu: "Ad Soyad")
  // Password kolonu eklenecek (örnek: yeni kolon ekle)
  return data['Ad Soyad'] === username && data['Şifre'] === password;
});

if (!user) {
  return [{
    json: {
      success: false,
      message: "Kullanıcı adı veya şifre hatalı!"
    }
  }];
}

// İzin günü hesapla
const sonGemis = user.json['Son Gemiş'];
let izinGunu = null;

if (sonGemis && sonGemis !== '') {
  // Tarih formatı: "15.12.2024" veya "15/12/2024"
  const parts = sonGemis.split(/[\./-]/);
  if (parts.length === 3) {
    const day = parseInt(parts[0]);
    const month = parseInt(parts[1]) - 1; // JS ay 0'dan başlar
    const year = parseInt(parts[2]);

    const sonGemisTarih = new Date(year, month, day);
    const bugun = new Date();

    // Gün farkı hesapla
    const fark = bugun - sonGemisTarih;
    izinGunu = Math.floor(fark / (1000 * 60 * 60 * 24));
  }
}

// Başarılı login
return [{
  json: {
    success: true,
    message: "Giriş başarılı!",
    personelData: {
      ...user.json,
      izinGunu: izinGunu,
      durumMesaji: izinGunu !== null ? `${izinGunu} gündür izinde` : "Gemide"
    }
  }
}];
