parameters.jsCode:
// Vapi Phone Number Configuration
// Bu workflow Twilio numaranızı Vapi'ye bağlar

const config = {
  // Twilio Credentials
  twilioAccountSid: 'TWILIO_ACCOUNT_SID', // Buraya Twilio SID'nizi yazın
  twilioAuthToken: 'TWILIO_AUTH_TOKEN',   // Buraya Twilio Auth Token'ınızı yazın
  twilioPhoneNumber: '+127223344653',      // Twilio telefon numaranız

  // Vapi Configuration
  vapiApiKey: 'YOUR_VAPI_API_KEY',         // Buraya Vapi API key'inizi yazın

  // Assistant Configuration
  assistantId: null, // Eğer önceden oluşturduysanız assistant ID

  // Webhook URLs (n8n webhook URL'leriniz)
  webhookBaseUrl: 'https://argusbot.duckdns.org/webhook',

  // Provider seçimi
  provider: 'twilio', // 'twilio' veya 'vapi' (Vapi kendi numarasını da verebilir)

  // Language ve Voice
  language: 'tr',
  voiceProvider: '11labs',
  voiceId: 'pNInz6obpgDQGcFmaJgB' // 11labs Turkish voice
};

return [{
  json: config
}];

parameters.body:
={{ JSON.stringify({
  provider: $json.provider,
  number: $json.twilioPhoneNumber,
  twilioAccountSid: $json.twilioAccountSid,
  twilioAuthToken: $json.twilioAuthToken,
  assistantId: $json.assistantId,
  name: 'ARGUS Customer Service Line',
  serverUrl: $json.webhookBaseUrl + '/vapi-customer-service',
  serverUrlSecret: 'n8n_vapi_webhook_secret'
}) }}

parameters.jsCode:
const response = $input.first().json;

console.log('Vapi Phone Number Created:', response);

const result = {
  success: true,
  phoneNumberId: response.id,
  phoneNumber: response.number,
  provider: response.provider,
  message: `Telefon numarası başarıyla Vapi'ye bağlandı! \nNumara: ${response.number}\nID: ${response.id}`
};

return [{
  json: result
}];
