const output = $input.first().json.stdout;
const parsed = JSON.parse(output);

return [{
  json: {
    success: true,
    phoneNumberId: parsed.id,
    phoneNumber: parsed.number,
    provider: parsed.provider,
    message: `✅ Telefon başarıyla Vapi'ye bağlandı!\n\nNumara: ${parsed.number}\nID: ${parsed.id}\nProvider: ${parsed.provider}`
  }
}];
