// Claude HTTP cevabından workflow JSON'unu çekip
// n8n API'nin anlayacağı apiWorkflow objesine çeviriyoruz.

const out = [];

for (const item of items) {
  const data = item.json || {};

  // 1) Claude messages cevabındaki text'i al
  let rawText = "";

  if (Array.isArray(data.content) && data.content.length > 0) {
    const textPart =
      data.content.find((p) => p.type === "text") || data.content[0];
    rawText = textPart.text || "";
  } else if (typeof data.completion === "string") {
    rawText = data.completion;
  } else if (typeof data.text === "string") {
    rawText = data.text;
  } else {
    rawText = JSON.stringify(data);
  }

  // 2) Metnin içindeki ilk '{' ile son '}' arasını JSON kabul et
  let wf;
  try {
    const start = rawText.indexOf("{");
    const end = rawText.lastIndexOf("}");

    if (start === -1 || end === -1 || end <= start) {
      throw new Error("Claude yanıtında JSON obje bulunamadı");
    }

    const jsonStr = rawText.slice(start, end + 1);
    wf = JSON.parse(jsonStr);
  } catch (err) {
    out.push({
      json: {
        error: "WORKFLOW_JSON_PARSE_FAILED",
        message: String(err),
        rawText,
      },
    });
    continue;
  }

  // 3) n8n workflow formatına çevir
  const apiWorkflow = {
    name: wf.name || "ARGUS auto: Flow",
    // active BİLEREK YOK, n8n read-only diye ağlamasın
    nodes: wf.nodes || [],
    connections: wf.connections || {},
    settings: wf.settings || {},
  };

  out.push({ json: { apiWorkflow } });
}

return out;

const item = items[0].json;
const data = item.data || item; // bazı sürümler data içinde döner

if (!data || !data.id) {
  return [
    {
      json: {
        ok: false,
        error: item.message || item.error || 'Workflow oluşturulamadı (id yok).',
        raw: item
      }
    }
  ];
}

return [
  {
    json: {
      ok: true,
      workflowId: data.id,
      name: data.name,
      url: `https://argusbot.duckdns.org/workflow/${data.id}`
    }
  }
];

