// MODE DETECTOR + SESSION ID + HISTORY
// POST body: { "message": "soru", "mode": "offline", "session_id": "abc", "history": [{"role":"user","content":"..."},{"role":"assistant","content":"..."}] }

const body = items[0].json.body || items[0].json || {};
const message = body.message || body.question || body.text || "";
const modeOverride = (body.mode || "auto").toLowerCase().trim();

const session_id = body.session_id || body.sessionId || body.session || `s_${Date.now()}`;

// Conversation history - client tarafindan gonderilir
const history = Array.isArray(body.history) ? body.history : [];

let mode = "offline";
if (modeOverride === "online") {
  mode = "online";
} else if (modeOverride === "offline") {
  mode = "offline";
} else {
  mode = "offline";
}

return [{
  json: {
    message,
    mode,
    session_id,
    history,
    original_body: body
  }
}];

// APPLY_ROUTE - parse + sanitize + session_id + history pass-through

function getRouterText(item) {
  return (item.json.content || item.json.message?.content || item.json.data?.message?.content || "").trim();
}

const raw = getRouterText(items[0] || { json: {} });
let obj = {};
try {
  let c = raw.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  obj = JSON.parse(c);
} catch (e) {
  const m = raw.match(/\{[\s\S]*\}/);
  if (m) { try { obj = JSON.parse(m[0]); } catch (_) {} }
}

let wbMsg = "";
try {
  wbMsg = $node["MODE_DETECT"].json.message || "";
} catch (_) {}

const question = (obj.question || wbMsg || "").trim();
const mode = $node["MODE_DETECT"].json.mode || "offline";
const session_id = $node["MODE_DETECT"].json.session_id || "default";
const history = $node["MODE_DETECT"].json.history || [];

if (!question) {
  return [{ json: { error: true, message: "Soru alınamadı. Lütfen tekrar deneyin." } }];
}

let collection = String(obj.collection || "ship_manuals_v3").trim().replace(/^=+/, "");
const allowed = new Set(["ship_electric","ship_ccr","ship_machinery","ship_bridge_equipment","ship_accommodation","ship_hull_piping","ship_hull_outfitting","ship_bwts_training","ship_8189_yard_dwg","ship_f"]);
if (!allowed.has(collection)) collection = "ship_machinery";

const validFolders = new Set(["ELECTRIC","CCR","BRIDGE_EQUIPMENT","MACHINERY","ACCOMMODATION","HULL PIPING","HULL OUTFITTING","BWTS TRAINING","8189 YARD DWG"]);
let folder = obj.folder ?? null;
if (typeof folder === "string") {
  folder = folder.trim().toUpperCase();
  if (!folder || !validFolders.has(folder)) folder = null;
}

let query_en = String(obj.query_en || "").trim();
if (!query_en) query_en = question;

return [{ json: { question, query_en, collection, folder, mode, session_id, history } }];

// BUILD_PROMPT v6 - Deduplication + simplified offline prompt + NotebookLLM style

const route = $node["APPLY_ROUTE"].json;
const question = route.question || "";
const mode = route.mode || "offline";
const session_id = route.session_id || "default";
const history = route.history || [];
const collection = route.collection || "unknown";

// --- SOURCE EXTRACTION ---
const cleaned = items
  .map((it) => it.json.document ? it.json.document : it.json)
  .map((d) => {
    let text = String(d.pageContent || d.page_content || d.text || "").trim();
    const md = d.metadata || {};

    let source = "";
    let page = "";
    let folder = "";
    let section = "";

    // 1) Embedded [KAYNAK: ...] header
    const headerMatch = text.match(/^\[KAYNAK:\s*(.+?)\s*\|\s*Sayfa:\s*(\d+)\s*\|\s*([^|\]]+)(?:\s*\|\s*([^\]]+))?\]/);
    if (headerMatch) {
      source = headerMatch[1].trim();
      page = parseInt(headerMatch[2]);
      folder = headerMatch[3].trim();
      if (headerMatch[4]) section = headerMatch[4].trim();
      text = text.substring(text.indexOf(']') + 1).trim();
    }

    // 2) Metadata fields
    if (!source) {
      source = md.sourceName || md.fileName || md.source_name || md.file_name
        || md.name || md.title || md.document_name || md.doc_name || "";
    }

    // 3) Path extraction
    if (!source || source === 'blob' || String(source).startsWith('blob:')) {
      const rawSource = String(md.source || md.file || md.path || "");
      if (rawSource && !rawSource.startsWith('blob:') && rawSource.length > 2) {
        const parts = rawSource.split('/').pop().split('\\').pop();
        if (parts && parts.length > 2) source = parts;
      }
    }

    // 4) Folder fallback
    if (!source || source === 'blob' || String(source).startsWith('blob:') || source === 'unknown') {
      const f = folder || md.folder || md.category || md.collection || "";
      if (f) {
        const folderMap = {
          'ELECTRIC': 'Elektrik Sistemi Manueli',
          'CCR': 'Kargo Kontrol Odasi Manueli',
          'MACHINERY': 'Makine Dairesi Manueli',
          'BRIDGE_EQUIPMENT': 'Kopru Ekipmanlari Manueli',
          'ACCOMMODATION': 'Yasam Alanlari Manueli',
          'HULL PIPING': 'Boru Hatlari Manueli',
          'HULL OUTFITTING': 'Gemi Donanimi Manueli',
          'BWTS TRAINING': 'Balast Suyu Aritma Manueli'
        };
        source = folderMap[f.toUpperCase()] || f + ' Manueli';
      } else {
        source = "Teknik Manuel";
      }
    }

    if (!page) page = md.page ?? md.pageNumber ?? md.loc?.pageNumber ?? md.page_number ?? "";
    if (!folder) folder = md.folder || md.category || "";
    if (!section) section = md.section || md.header || md.heading || md.chapter || "";

    return { text, source, folder, page, section };
  })
  .filter(h => h.text.length > 20)
  .filter(h => !/^[\W_]+$/.test(h.text));

// --- DEDUPLICATION: Remove chunks with >70% word overlap ---
const deduped = [];
for (const chunk of cleaned) {
  let isDuplicate = false;
  const words1 = new Set(chunk.text.toLowerCase().split(/\s+/).filter(w => w.length > 3));
  for (const existing of deduped) {
    const words2 = new Set(existing.text.toLowerCase().split(/\s+/).filter(w => w.length > 3));
    const intersection = [...words1].filter(w => words2.has(w));
    const similarity = intersection.length / Math.min(words1.size, words2.size);
    if (similarity > 0.7) {
      isDuplicate = true;
      break;
    }
  }
  if (!isDuplicate) deduped.push(chunk);
}

const maxChunks = mode === "online" ? 12 : 10;
const top = deduped.slice(0, maxChunks);

// --- HARD GUARD: No context = no LLM ---
if (top.length === 0) {
  const collectionNames = {
    'ship_electric': 'Elektrik',
    'ship_ccr': 'Kargo Kontrol (CCR)',
    'ship_machinery': 'Makine Dairesi',
    'ship_bridge_equipment': 'Kopru Ekipmanlari',
    'ship_accommodation': 'Yasam Alanlari',
    'ship_hull_piping': 'Boru Hatlari',
    'ship_hull_outfitting': 'Gemi Donanimi',
    'ship_bwts_training': 'Balast Suyu Aritma',
    'ship_8189_yard_dwg': 'Tersane Cizimleri',
    'ship_f': 'SOLAS/Egitim'
  };
  const colName = collectionNames[collection] || collection;
  return [{
    json: {
      question,
      context_found: false,
      text: `Bu konuda ${colName} dokumanlarinda bilgi bulunamadi.\n\nOneri: Soruyu farkli kelimelerle tekrar deneyin.`,
      sources: "Kaynak bulunamadi",
      prompt: "",
      mode,
      session_id,
      debug_collection: collection,
      debug_chunk_count: 0,
      debug_deduped: 0
    }
  }];
}

// --- Build context ---
const context = top.map((h, i) => {
  const pg = h.page ? ` | Sayfa: ${h.page}` : "";
  const sec = h.section ? ` | ${h.section}` : "";
  return `[Kaynak ${i+1}] ${h.source}${pg}${sec}\n${h.text}`;
}).join("\n---\n");

const sources = top.map((h, i) => {
  const pg = h.page ? ` - Sayfa ${h.page}` : "";
  const sec = h.section ? ` - ${h.section}` : "";
  return `${i+1}. ${h.source}${pg}${sec}`;
}).join("\n");

const sourceList = top.map((h, i) => {
  const pg = h.page ? ` - Sayfa ${h.page}` : "";
  const sec = h.section ? ` - ${h.section}` : "";
  return `[Kaynak ${i+1}] ${h.source}${pg}${sec}`;
}).join("\n");

// --- Different prompts for offline vs online ---
let prompt;

if (mode === "online") {
  // ONLINE: Claude can handle complex prompts
  prompt = `Sen MANTA ANADOLU gemisinin teknik uzmani ARGUS'sun.

## KESIN KURALLAR
1. SADECE asagidaki dokuman icerigindeki bilgiyi kullan. UYDURMA!
2. Dokumanda bilgi yoksa "Bu konuda dokumanlarda bilgi bulunamadi" de.
3. Her bilgiyi kaynagiyla ver: [Kaynak X]
4. Teknik terimlerin Ingilizcesini parantez icinde yaz.

## KAYNAKLAR
${sourceList}

## CEVAP FORMATI

### Sorun Analizi
1-2 cumle teknik ozet.

### Kontrol Edilecek Noktalar
1. [Kontrol noktasi] - [Kaynak X, Sayfa Y]
   * Teknik detay
2. ...

### Adim Adim Yapilacaklar
1. ...

### Kaynaklar
Dosya adi ve sayfa numarasi ile.

SORU: ${question}

--- DOKUMANLAR (${top.length} parca) ---
${context}
--- SON ---

UYARI: Dokumanda OLMAYAN bilgiyi EKLEME.`;

} else {
  // OFFLINE: llama3.1 icin COKK basit prompt
  prompt = `Asagidaki DOKUMAN parcalarini oku. SADECE bu parcalardaki bilgiyi kullanarak soruyu cevapla.

Her bilginin yanina kaynak numarasini yaz: [Kaynak 1], [Kaynak 2] gibi.
Dokumanda bilgi yoksa "Dokumanlarda bu konuyla ilgili bilgi bulunamadi" de.
Bilgiyi UYDURMA, sadece dokumandakini yaz.

KAYNAKLAR:
${sourceList}

SORU: ${question}

DOKUMANLAR:
${context}

CEVAP:`;
}

return [{
  json: {
    question, context, sources, prompt, mode, session_id,
    context_found: true,
    debug_collection: collection,
    debug_chunk_count: top.length,
    debug_deduped: cleaned.length - deduped.length
  }
}];

// Claude API response parse
const input = items[0].json;
let text = "Cevap uretilemedi.";

try {
  if (input.content && Array.isArray(input.content) && input.content[0]) {
    text = input.content[0].text || text;
  } else if (input.error) {
    text = "Online API hatasi: " + JSON.stringify(input.error);
  }
} catch (e) {
  text = "API yaniti parse edilemedi: " + e.message;
}

return [{ json: { text, sources: $node["BUILD_PROMPT"].json.sources || "", question: $node["BUILD_PROMPT"].json.question || "" } }];
