// =====================================================
// AYARLAR - HER KLASOR ICIN BURADAN DEGISTIR
// =====================================================
// Her calistirmada sadece BIR klasor yukle.
// Bitince collection adini ve folderPath'i degistirip tekrar calistir.
//
// YUKLEME SIRASI (oneri):
// 1) MACHINERY       -> ship_machinery
// 2) ACCOMMODATION   -> ship_accommodation
// 3) HULL PIPING     -> ship_hull_piping
// 4) HULL OUTFITTING -> ship_hull_outfitting
// 5) BWTS TRAINING   -> ship_bwts_training
// 6) F               -> ship_f
// 7) 8189 YARD DWG   -> ship_8189_yard_dwg
// 8) BRIDGE_ EQUIPMENT -> ship_bridge_equipment  (en buyuk, en son)
//
// NOT: ELECTRIC ve CCR zaten yuklu, tekrar yukleme!
// =====================================================

const CONFIG = {
  folderPath: "D:\\MANTA ANADOLU\\MACHINERY",
  collection: "ship_machinery",
  folderTag: "MACHINERY",
  chunkSize: 800,
  chunkOverlap: 150
};

return [{ json: CONFIG }];

// PowerShell ciktisini satirlara bol, her PDF bir item olsun
const config = $node["CONFIG"].json;
const stdout = items[0].json.stdout || "";
const lines = stdout.split(/\r?\n/).map(l => l.trim()).filter(l => l.length > 0 && l.toLowerCase().endsWith('.pdf'));

if (lines.length === 0) {
  throw new Error("Klasorde PDF bulunamadi: " + config.folderPath);
}

return lines.map((filePath, index) => {
  const fileName = filePath.split('\\').pop().replace('.pdf', '').replace('.PDF', '');
  return {
    json: {
      filePath,
      fileName,
      index: index + 1,
      total: lines.length,
      collection: config.collection,
      folderTag: config.folderTag,
      chunkSize: config.chunkSize,
      chunkOverlap: config.chunkOverlap
    }
  };
});

// SMART CHUNKER - Overlap ile akilli parcalama + metadata

const loopItem = $node["Tek Tek Yukle"].json;
const text = items[0].json.text || items[0].json.data || "";

if (!text || text.length < 50) {
  return [{
    json: {
      pageContent: "[Bu PDF'den metin cikarilmadi: " + loopItem.fileName + "]",
      metadata: {
        sourceName: loopItem.fileName,
        fileName: loopItem.fileName,
        source: loopItem.fileName,
        folder: loopItem.folderTag,
        page: 0,
        chunk: 0
      }
    }
  }];
}

const chunkSize = loopItem.chunkSize || 800;
const overlap = loopItem.chunkOverlap || 150;
const chunks = [];

const pages = text.split(/\f/);
if (pages.length <= 1) {
  pages.length = 0;
  pages.push(text);
}

let globalChunkIndex = 0;

for (let pageIdx = 0; pageIdx < pages.length; pageIdx++) {
  const pageText = pages[pageIdx].trim();
  if (pageText.length < 30) continue;

  let pos = 0;
  while (pos < pageText.length) {
    let end = Math.min(pos + chunkSize, pageText.length);

    if (end < pageText.length) {
      const lastPeriod = pageText.lastIndexOf('.', end);
      const lastNewline = pageText.lastIndexOf('\n', end);
      const bestBreak = Math.max(lastPeriod, lastNewline);
      if (bestBreak > pos + chunkSize * 0.4) {
        end = bestBreak + 1;
      }
    }

    const chunkText = pageText.substring(pos, end).trim();

    if (chunkText.length > 30) {
      chunks.push({
        json: {
          pageContent: chunkText,
          metadata: {
            sourceName: loopItem.fileName,
            fileName: loopItem.fileName,
            source: loopItem.fileName,
            folder: loopItem.folderTag,
            page: pageIdx + 1,
            chunk: globalChunkIndex
          }
        }
      });
      globalChunkIndex++;
    }

    pos = end - overlap;
    if (pos <= 0 && end >= pageText.length) break;
    if (end >= pageText.length) break;
  }
}

if (chunks.length === 0) {
  return [{
    json: {
      pageContent: "PDF icerigi bos: " + loopItem.fileName,
      metadata: {
        sourceName: loopItem.fileName,
        fileName: loopItem.fileName,
        source: loopItem.fileName,
        folder: loopItem.folderTag,
        page: 0,
        chunk: 0
      }
    }
  }];
}

return chunks;
