// Gemini Imagen API'den gelen base64 stringini al
const input = $input.first().json;
let base64String;

// Gemini Imagen API response formatÄ±: predictions[0].bytesBase64Encoded
if (input.predictions && input.predictions[0]) {
  if (input.predictions[0].bytesBase64Encoded) {
    base64String = input.predictions[0].bytesBase64Encoded;
  } else if (input.predictions[0].image && input.predictions[0].image.bytesBase64Encoded) {
    base64String = input.predictions[0].image.bytesBase64Encoded;
  }
}

// Data URI prefix'ini temizle (eÄŸer varsa)
if (base64String && base64String.startsWith('data:image/')) {
  const commaIndex = base64String.indexOf(',');
  if (commaIndex !== -1) {
    base64String = base64String.substring(commaIndex + 1);
  }
}

return [{
  json: {
    base64_data: base64String
  }
}];

